if (window[APP_ID] && window[APP_ID].unmount) {
  window[APP_ID].unmount();
}

const GRID =
  "data:image/png;base64,";

const state = {
  settings: false,
  fixed: false,
  position: { x: 0, y: 0 },
  draft: null,
  start: null
};

const wrapper = document.createElement("div");
wrapper.setAttribute(
  "style",
  `
    position: absolute;
    z-index: 999999;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
  
    display: flex;
    justify-content: center;
  
    pointer-events: none;
  `
);

const image = document.createElement("img");
image.setAttribute(
  "style",
  `
      opacity: .25;
      pointer-events: none;
    `
);
wrapper.appendChild(image);

const selectImage = () => {
  const fileSelector = document.createElement("input");
  fileSelector.setAttribute("type", "file");
  fileSelector.addEventListener("change", function(event) {
    const src = window.URL.createObjectURL(this.files[0]);
    image.setAttribute("src", src);
  });
  fileSelector.click();
};

const handleKeyDown = function(event) {
  if (event.keyCode === 17) {
    state.settings = !state.settings;
    wrapper.style.pointerEvents = state.settings ? null : "none";
  } else if (state.settings && event.keyCode === 49) {
    state.position = { x: 0, y: 0 };
    image.style.transform = `
        translateX(${state.position.x}px)
        translateY(${state.position.y}px)
      `;
  } else if (state.settings && event.keyCode === 50) {
    state.fixed = !state.fixed;
    wrapper.style.position = state.fixed ? `fixed` : "absolute";
  } else if (state.settings && event.keyCode === 51) {
    state.filter = !state.filter;
    image.style.filter = state.filter ? `invert(100%)` : null;
  } else if (state.settings && event.keyCode === 57) {
    image.setAttribute("src", GRID);
  } else if (state.settings && event.keyCode === 48) {
    selectImage();
  } else if (state.settings && event.keyCode === 81) {
    window[APP_ID].unmount();
  }
};

const handleMouseDown = function(event) {
  if (state.settings) {
    state.draft = { ...state.position };
    state.start = { x: event.clientX, y: event.clientY };
  }
};

const handleMouseUp = function(event) {
  if (state.settings) {
    state.position = { ...state.draft };
    state.draft = null;
    state.start = null;
  }
};

const handleMouseMove = function(event) {
  if (state.settings && state.start) {
    state.draft = {
      x: state.position.x + event.clientX - state.start.x,
      y: state.position.y + event.clientY - state.start.y
    };
    image.style.transform = `
      translateX(${state.draft.x}px)
      translateY(${state.draft.y}px)
    `;
  }
};

document.addEventListener("keydown", handleKeyDown);
document.addEventListener("mousedown", handleMouseDown);
document.addEventListener("mouseup", handleMouseUp);
document.addEventListener("mousemove", handleMouseMove);
document.body.appendChild(wrapper);

if (!window[APP_ID]) {
  window[APP_ID] = {};
}

window[APP_ID].unmount = () => {
  document.removeEventListener("keydown", handleKeyDown);
  document.removeEventListener("mousedown", handleMouseDown);
  document.removeEventListener("mouseup", handleMouseUp);
  document.removeEventListener("mousemove", handleMouseMove);
  wrapper.remove();
  window[APP_ID].unmount = null;
};
